import { BeerService } from './beer.service';
import { Component, OnInit } from '@angular/core';
import { Beer } from './beer';
import { HttpErrorResponse } from '@angular/common/http';
import { NgbCollapse } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  title = 'beermanagerapp';
  public beers!: Beer[];
  isSearchInputVisible: boolean = false;
  searchQuery: string = '';
  isNavbarCollapsed = true;

  toggleSearchInput() {
    this.isNavbarCollapsed = !this.isNavbarCollapsed;
    this.isSearchInputVisible = !this.isSearchInputVisible;
    if (this.isSearchInputVisible) {
      setTimeout(() => {
        const searchInput = document.getElementById('search-input');
        if (searchInput) {
          searchInput.focus();
        }
      });
    }
  }

  constructor(private BeerService: BeerService, public collapse: NgbCollapse) {}

  ngOnInit(): void {
    this.getBeers();
  }

  public getBeers(): void {
    this.BeerService.getBeers().subscribe(
      (response: Beer[]) => {
        this.beers = response;
      },
      (err: HttpErrorResponse) => {
        alert(err.message);
      }
    );
  }
}
