import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-star-rating',
  templateUrl: './star-rating.component.html',
  styleUrls: ['./star-rating.component.css'],
})
export class StarRatingComponent {
  @Input()
  rating!: number;
  stars: number[] = [1, 2, 3, 4, 5];
  calculateStars() {
    const filledStars = Math.floor(this.rating);
    const hasHalfStar = this.rating % 1 !== 0;
    const halfStar = hasHalfStar ? 1 : 0;
    const emptyStars = 5 - filledStars - halfStar;

    return {
      filled: filledStars,
      half: hasHalfStar,
      empty: emptyStars,
    };
  }
}
<div class="beer-rating">
  <ng-container *ngFor="let star of stars">
    <span class="star" [class.filled]="star <= calculateStars().filled"
      >&#9733;</span
    >
    <span class="star half" *ngIf="calculateStars().half">&#9733;</span>
    <span class="star" [class.filled]="star <= calculateStars().empty"
      >&#9733;</span
    >
  </ng-container>
</div>
